import React, { Suspense, lazy } from 'react';
import { Route, Switch } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';
import { withRouter } from 'react-router'
import BackgroundImage from './images/home/studio.jpg';
import Spinner from './spinner';

import './css/app.css';

import CheckoutForm from './shop/components/checkout';

import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe("pk_test_0kzCXvF6peItv0Z0FZ6QFG0j");


const Contact = lazy(() => import('./contact'));
const Home = lazy(() => import('./home'));
const NotFoundPage = lazy(() => import('./NotFoundPage'));
const Shop = lazy(() => import('./shop'));
const BB = lazy(() => import('./BB'));

const App = ({ location }) => {
  return (
    <>
      <Navbar location={location} />
      <div className="alert alert-warning alert-dismissible fade show text-center mt-2" role="alert" style={{
        position: 'fixed',
        zIndex: '100',
        width: '100vw',
        bottom: '0%',
        margin: '0'
      }}>
        <div className="d-flex flex-wrap align-items-center justify-content-center">
          <svg className="bi bi-shield-lock" width="3em" height="3em" viewBox="0 0 16 16" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
            <path fill-rule="evenodd" d="M5.443 1.991a60.17 60.17 0 00-2.725.802.454.454 0 00-.315.366C1.87 7.056 3.1 9.9 4.567 11.773c.736.94 1.533 1.636 2.197 2.093.333.228.626.394.857.5.116.053.21.089.282.11A.73.73 0 008 14.5c.007-.001.038-.005.097-.023.072-.022.166-.058.282-.111.23-.106.525-.272.857-.5a10.197 10.197 0 002.197-2.093C12.9 9.9 14.13 7.056 13.597 3.159a.454.454 0 00-.315-.366c-.626-.2-1.682-.526-2.725-.802C9.491 1.71 8.51 1.5 8 1.5c-.51 0-1.49.21-2.557.491zm-.256-.966C6.23.749 7.337.5 8 .5c.662 0 1.77.249 2.813.525a61.09 61.09 0 012.772.815c.528.168.926.623 1.003 1.184.573 4.197-.756 7.307-2.367 9.365a11.191 11.191 0 01-2.418 2.3 6.942 6.942 0 01-1.007.586c-.27.124-.558.225-.796.225s-.526-.101-.796-.225a6.908 6.908 0 01-1.007-.586 11.192 11.192 0 01-2.417-2.3C2.167 10.331.839 7.221 1.412 3.024A1.454 1.454 0 012.415 1.84a61.11 61.11 0 012.772-.815z" clip-rule="evenodd" />
            <path d="M9.5 6.5a1.5 1.5 0 11-3 0 1.5 1.5 0 013 0z" />
            <path d="M7.411 8.034a.5.5 0 01.493-.417h.156a.5.5 0 01.492.414l.347 2a.5.5 0 01-.493.585h-.835a.5.5 0 01-.493-.582l.333-2z" />
          </svg>
          <p className="serif">
            We use cookies on this site to enhance your user experience. For complete overview of all cookies used, please visit Private Policy.
          </p>
        </div>
        <button type="button" className="close" data-dismiss="alert" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <Suspense fallback={<Spinner />}>
        <div className="overflow-hidden" style={{ minHeight: '85vh' }}>
          <img src={BackgroundImage} className="img-fluid h-100 w-100" alt="background" style={
            {
              top: "0px",
              zIndex: "-1",
              position: "fixed",
              left: "50%",
              transform: "translate(-50%, 0)",
              maxWidth: '2000px'
            }}
          />
          <Switch>
            <Route path="/" exact component={Home} />
            <Route path="/contact" exact component={Contact} />
            <Route path="/store" exact component={Shop} />
            <Route path="/bb" exact component={BB} />
            {location.pathname !== "/store/checkout" && <Route component={NotFoundPage} />}
          </Switch>
          <Elements stripe={stripePromise}>
            <Route path="/store/checkout" exact component={CheckoutForm} />
          </Elements>
        </div>
      </Suspense>
      <Footer />
    </>
  );
};

export default withRouter(App);
