import React from 'react';
import { Switch } from 'react-router-dom';
import {StripeProvider} from 'react-stripe-elements';

import Header from './Header';
import Footer from './Footer';
import Contact from './contact/Contact';
import Home from './Home';
import Team from './Team';
import ContactList from './contact/ContactList';
import Shop from './shop';
import FloatCart from './shop/components/floatcart';
import Checkout from './shop/components/checkout';
import {withRouter} from 'react-router'
import './css/app.css';

const App = ({location}) => {
  return (
    <>
        <Header />
        <main className="inner cover">
          {location.pathname !== "/shop/checkout" && <FloatCart />}
          <Switch>
            <Route path="/" exact component={Home} />
            <Route path="/contact" exact component={Contact} />
            <Route path="/shop" exact component={Shop} />
            <StripeProvider apiKey="pk_test_0kzCXvF6peItv0Z0FZ6QFG0j" >
              <Route path="/shop/checkout" exact component={Checkout} />
            </StripeProvider>
            <Route path="/team" exact component={Team} />
            <Route path="/ContactList" extact component={ContactList} />
          </Switch>
        </main>
        <Footer />
        </>
  );
};

export default withRouter(App);
