import React from 'react';
import CardSection from './CardSection';
import {
	CardElement,
  CardNumberElement,
  CardExpiryElement,
  CardCVCElement,
  PaymentRequestButtonElement,
  IbanElement,
  IdealBankElement,
  StripeProvider,
  Elements,
  injectStripe,
} from 'react-stripe-elements';

import './css/style.css';


const handleBlur = () => {
console.log('[blur]');
};
const handleChange = (change) => {
console.log('[change]', change);
};
const handleClick = () => {
console.log('[click]');
};
const handleFocus = () => {
console.log('[focus]');
};
const handleReady = () => {
console.log('[ready]');
};

const createOptions = (fontSize, padding) => {
return {
style: {
 base: {
   fontSize,
   color: '#424770',
   letterSpacing: '0.025em',
   fontFamily: 'Source Code Pro, monospace',
   '::placeholder': {
     color: '#aab7c4',
   },
   padding,
 },
 invalid: {
   color: '#9e2146',
 },
},
};
};


class CheckoutForm extends React.Component {
  handleSubmit = (ev) => {
     ev.preventDefault();
     if (this.props.stripe) {
       this.props.stripe
         .createToken()
         .then((payload) => console.log('[token]', payload));
     } else {
       console.log("Stripe.js hasn't loaded yet.");
     }
   };
   render() {
     return (
       <form onSubmit={this.handleSubmit}>
       <label>
  Name
  <input name="name" type="text" placeholder="Jane Doe" required />
</label>
<label>
  Email
  <input
    name="email"
    type="email"
    placeholder="jane.doe@example.com"
    required
  />
</label>
         <label>
           Card number
           <CardNumberElement
             onBlur={handleBlur}
             onChange={handleChange}
             onFocus={handleFocus}
             onReady={handleReady}
             {...createOptions(this.props.fontSize)}
           />
         </label>
         <label>
           Expiration date
           <CardExpiryElement
             onBlur={handleBlur}
             onChange={handleChange}
             onFocus={handleFocus}
             onReady={handleReady}
             {...createOptions(this.props.fontSize)}
           />
         </label>
         <label>
           CVC
           <CardCVCElement
             onBlur={handleBlur}
             onChange={handleChange}
             onFocus={handleFocus}
             onReady={handleReady}
             {...createOptions(this.props.fontSize)}
           />
         </label>
         <button>Pay</button>
       </form>
     );
   }
}


export default injectStripe(CheckoutForm);
