import React from 'react';
import Together from '../../images/home/together.jpg';
import ShaneBack from '../../images/home/shaneback.jpg';

const Attraction = ({scrollHeight}) => {

    let imgStyle = {
            position: "relative",
            bottom: '190px'
    };

    if(scrollHeight > .03 && scrollHeight < .38){
        console.log(scrollHeight)
        imgStyle = {
            position: "relative",
            bottom: `${190 - (scrollHeight * 500)}px`
        }
    }else if(scrollHeight > .38){
        imgStyle = {
            position: "relative",
            bottom: "0"
        }
    }

    return (
        <>
        <div className="pt-6 pb-6" id="store" style={{color: '#332212', backgroundColor: '#fff'}}>
            <div className="row m-auto justify-content-center align-items-center">
            <div className="col">
            <img src={ShaneBack} className="rounded img-fluid"/>
            </div>
            <div className="col">
                <h1 className="text-center" style={{ fontSize: '3.5rem' }}>New Podcast Every Week</h1>
                <div id="chevron" style={{ width: '70%', top: '40px' }}/>
            </div>
            </div>
        </div>
        </>
    )
}

export default Attraction;
