import React from 'react';
import {Modal , Button} from 'react-bootstrap';
import { useState } from 'react';
import history from '../history';

import './css/modal.css';

const MyModal = ( props ) => {
    const [show, setShow] = useState(props.showValue);

    console.log(props);
    const handleClose = () => {
      setShow(false);
      history.push('/');
    }

    const handleButton = () => {
      if(props.handleState){
        props.handleState();
      }

      setShow(false);
      history.push(props.closeDirect);
    }

    // const handleShow = () => {
    //   setShow(true)
    // };

    const style = {
      color: 'red',
      position: 'relative',
      bottom: '2px',
      right: '2px'
    }

    const svg = () => {
      switch (svgType) {
        case 'success':
          return (
            <svg className="bi bi-alert-octagon" style = {style} width="1.5em" height="1.5em" viewBox="0 0 20 20" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
              <path fill-rule="evenodd" d="M6.54 2.146A.5.5 0 016.893 2h6.214a.5.5 0 01.353.146l4.394 4.394a.5.5 0 01.146.353v6.214a.5.5 0 01-.146.353l-4.394 4.394a.5.5 0 01-.353.146H6.893a.5.5 0 01-.353-.146L2.146 13.46A.5.5 0 012 13.107V6.893a.5.5 0 01.146-.353L6.54 2.146zM7.1 3L3 7.1v5.8L7.1 17h5.8l4.1-4.1V7.1L12.9 3H7.1z" clip-rule="evenodd"></path>
              <rect width="2" height="2" x="9.002" y="12" rx="1"></rect>
              <path d="M9.1 6.995a.905.905 0 111.8 0l-.35 3.507a.553.553 0 01-1.1 0L9.1 6.995z"></path>
            </svg>
          )
          break;
        case 'warning':
          return (
            <svg className="bi bi-check" width="1em" height="1em" viewBox="0 0 20 20" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
              <path fill-rule="evenodd" d="M15.854 5.646a.5.5 0 010 .708l-7 7a.5.5 0 01-.708 0l-3.5-3.5a.5.5 0 11.708-.708L8.5 12.293l6.646-6.647a.5.5 0 01.708 0z" clip-rule="evenodd">
              </path>
            </svg>
          )
          break;
        default:

      }
    }

    return (
      <>
        <Modal show={show}
          onHide={handleClose}
          aria-labelledby="contained-modal-title-vcenter"
          centered
          dialogClassName={"contactModal"}
          >
          <Modal.Header closeButton>
            <Modal.Title>
            {svg}
            {props.title}
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>{props.description}</Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={handleButton}>
              {props.buttonName}
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
}

export default MyModal;
