import React from 'react';
import Together from '../../images/home/together.jpg';
import Facebook from '../../images/social/facebook.png';
import Instagram from '../../images/social/insta.png';
import Twitter from '../../images/social/twitter.png';
import Linkedin from '../../images/social/linkedin.png';

const Attraction = ({scrollHeight}) => {

    let imgStyle = {
            position: "relative",
            bottom: '190px'
    };

    if(scrollHeight > .03 && scrollHeight < .38){
        console.log(scrollHeight)
        imgStyle = {
            position: "relative",
            bottom: `${190 - (scrollHeight * 500)}px`
        }
    }else if(scrollHeight > .38){
        imgStyle = {
            position: "relative",
            bottom: "0"
        }
    }

    return (
        <>
        <div className="pt-6 pb-6" id="store" style={{color: '#332212', backgroundColor: '#fff'}}>
            <div className="row m-auto justify-content-center align-items-center">
            <div className="col">
            <img src={Together} className="rounded img-fluid shadow"/>
            </div>
            <div className="col">
                <h1 className="text-center" style={{ fontSize: '3rem' }}>Follow Us</h1>
                <ul className="list-inline social-buttons text-center">
              <li className="list-inline-item">
                <a href="#">
                  <i className="fab fa-twitter"><img className="team-icons" src={Twitter} style={{}}/></i>
                </a>
              </li>
              <li className="list-inline-item">
              <a href="https://www.facebook.com/booksandbrewskies/" target="_blank" rel="noopener noreferrer">
                  <i className="fab fa-facebook-f"><img className="team-icons" src={Facebook} /></i>
                </a>
              </li>
              <li className="list-inline-item">
              <a href="#" target="_blank" rel="noopener noreferrer">
                  <i className="fab fa-linkedin-in"><img className="team-icons" src={Linkedin} /></i>
                </a>
              </li>
              <li className="list-inline-item">
              <a href="https://www.instagram.com/booksandbrewskies/" target="_blank" rel="noopener noreferrer">
                  <i className="fab fa-instagram-in"><img className="team-icons" src={Instagram} /></i>
                </a>
              </li>
            </ul>
                <div id="chevron" style={{ width: '100%', top: '40px', minWidth: '200px' }}/>
            </div>
            </div>
        </div>
        </>
    )
}

export default Attraction;
