import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

class Checkout extends React.Component {
  componentDidMount() {
    this.updateCart();
  }

  updateCart = () => {
    const cartProductsLs = JSON.parse(localStorage.getItem('cartProducts'));
    const cartTotalLs = JSON.parse(localStorage.getItem('cartTotal'));
    const { cartProducts, cartTotal } = this.props;
      if(cartProductsLs && cartTotalLs){
        if(cartProductsLs.length > 0){
          Object.assign(cartProducts, cartProductsLs);
          Object.assign(cartTotal, cartTotalLs);
        }
      }
    }

    render(){
      console.log(this.props.cartProducts);
      return (
      <React.Fragment>
        {this.props.cartProducts}
      </React.Fragment>
    )
  }
}

const mapStateToProps = state => {
  return {
  cartProducts: state.cart.products,
  cartTotal: state.total.data
  }
}

export default connect(mapStateToProps,{})(Checkout);
