import React, { useState } from 'react';
import { isEmpty } from 'lodash';

const AddComment = ({itemName, itemId, itemComments, itemImg, handleClose}) => {
    const [name, setName] = useState('');
    const [comment, setComment] = useState('');

    const handleFormSubmit = (e) => {
        e.preventDefault();

        console.log(name, comment);
    }

    console.log(itemComments)

    let mapComments = [];

    if(!isEmpty(itemComments)){
        mapComments = itemComments.map((item, index) => {
            return (
                <div className="media" key={index}>
                    <img src="./media/logo.png" className="mr-3" alt="..." />
                    <div className="media-body">
                        <p className="mt-0 mb-0" style={{fontSize: "1rem"}}>{item.name}</p>
                        <p className="text-muted">{item.comment}</p>
                    </div>
                </div>
            )
        });               
    }

    return (
        <div className="modal d-flex" tabIndex="-1" role="dialog">
            <div className="modal-dialog modal-dialog-centered w-100" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <div className="row m-auto">
                            <div className="col"><h5 className="modal-title">{itemName}</h5></div>
                            <div className="col"><img src={itemImg} className="img-fluid" width="100px" height="200px" alt="item image" /></div>
                        </div>
                        <button type="button" className="close" aria-label="Close" onClick={handleClose}>
                            <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <form onSubmit={handleFormSubmit}>
                        <div className="modal-body">
                            {(!isEmpty(mapComments)) ? mapComments : <p>No Comments</p>}
                            <div className="form-group">
                                <input type="text"
                                    className="form-control"
                                    placeholder="Name" name="Name"
                                    onChange={(e) => setName(e.target.value)}
                                    value={name}
                                    required />
                            </div>
                            <div className="form-group">
                                <textarea type="text"
                                    className="form-control"
                                    maxLength='500'
                                    placeholder="What do you think?"
                                    name="Comment"
                                    onChange={(e) => setComment(e.target.value)}
                                    value={comment} style={{ minHeight: '150px' }}
                                    required />
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="submit" className="btn btn-primary btn-block">Submit</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default AddComment;
