//import contacts from '../apis/api';
import jsonApi from '../apis/api';
// import history from '../history';
import { FETCH_PRODUCTS } from './actionTypes';
import { LOAD_CART, ADD_PRODUCT, REMOVE_PRODUCT, CHANGE_PRODUCT_QUANTITY } from './actionTypes';


export const contact = (formValues) => async (dispatch, getState) => {

  // const response = await contacts.post('/contacts/add', {...formValues});
  const response = await jsonApi.post('/contacts', {...formValues});

  dispatch({ type: 'CONTACTED', payload: response.data });

  // history.push('/contact?success');
};

export const fetchProducts = () => async dispatch => {
  const response = await jsonApi.get('/products')

  dispatch({ type: FETCH_PRODUCTS, payload: response.data });
}
