import React from 'react';
import {ElementsConsumer, CardElement} from '@stripe/react-stripe-js';
import { connect } from 'react-redux';
import CardSection from './components/CardSection';
import IdentitySection from './components/IdentitySection';
import AddressSection from './components/AddressSection';
import './css/CheckoutFormStyles.css';
import axios from 'axios';
import Spinner from '../../../spinner'
import CartSection from './components/CartSection';
import { updateCart } from '../util';

class CheckoutForm extends React.Component {

  state = {
    name: '',
    email: '',
    phone: '',
    line1: '',
    city: '',
    state: '',
    zip: '',
    client_secret: '',
  }

  componentDidMount = async() => {
    // let response;
    // try {
    //  response = await axios.get('/shop/checkout');
    // } catch (e) {
    // return console.log(e);
    // }
    //
    // if(response.data){
    //   this.setState({client_secret: response.data})
    // }
    const {cartTotal, cartProducts} = this.props;

    updateCart(cartProducts, cartTotal);

  }

  handleSubmit = async (event) => {
    // We don't want to let default form submission happen here,
    // which would refresh the page.
    event.preventDefault();

    console.log("Called");

    const {stripe, elements} = this.props
    const {name, phone, email, line1, city, state, zip} = this.state;

    if (!stripe || !elements) {
      // Stripe.js has not yet loaded.
      // Make  sure to disable form submission until Stripe.js has loaded.
      return;
    }

    const result = await stripe.confirmCardPayment(this.state.client_secret, {
      payment_method: {
        card: elements.getElement(CardElement),
        billing_details: {
          name: name,
          phone: phone,
          email: email,
          line1: line1,
          city: city,
          state: state,
          zip: zip
        },
      }
    });

    if (result.error) {
      // Show error to your customer (e.g., insufficient funds)
      console.log(result.error.message);
    } else {
      // The payment has been processed!
      if (result.paymentIntent.status === 'succeeded') {
        // Show a success message to your customer
        // There's a risk of the customer closing the window before callback
        // execution. Set up a webhook or plugin to listen for the
        // payment_intent.succeeded event that handles any business critical
        // post-payment actions.
        const storeOrder = await axios.post('/shop/checkout/success');
        storeOrder
          .then()
          .catch()

          //Display message
          //Payment Succeeded and StoredOrder in the database
      }
    }
  };

  handleInputChange = (event) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  render() {
    const { name, phone, email, line1, city, state, zip } = this.state;
    const { cartTotal, cartProducts } = this.props;

    // if(this.state.client_secret === ''){
    //   return <Spinner />;
    // }
    return (
      <div className="row">
      <div className="col-md-4 order-md-2 mb-4">
        <CartSection cartTotal={cartTotal} cartProducts={cartProducts} />
      </div>
      <div className="col-md-8 order-md-1">
        <form onSubmit={this.handleSubmit}>
          <IdentitySection name={name} phone={phone} email={email} handleInputChange={this.handleInputChange}/>
          <AddressSection line1={line1} city={city} state={state} zip={zip} handleInputChange={this.handleInputChange} />
          <CardSection />
          <button className="btn btn-primary" disabled={!this.props.stripe}>Confirm order</button>
        </form>
      </div>
    </div>
    );
  }
}

const InjectedCheckoutForm = ({cartProducts, cartTotal}) => {
  return (
    <ElementsConsumer>
      {({stripe, elements}) => (
        <CheckoutForm  stripe={stripe}
          elements={elements}
          cartProducts={cartProducts}
          cartTotal={cartTotal}
          />
      )}
    </ElementsConsumer>
  );

};

const mapStateToProps = (state) => ({
  cartTotal: state.total.data,
  cartProducts: state.cart.products
});

export default connect(mapStateToProps, {})(InjectedCheckoutForm);
