import React from 'react';
import { Field, reduxForm } from 'redux-form';

class ContactForm extends React.Component {
  renderError({ error, touched }) {
    if (touched && error) {
      return (
        <div className="invalid-feedback" style={{marginBottom: ".25rem"}}>
          {error}
        </div>
      )
    }
  }

  renderInput = ({ input, label, meta }) => {
    const className = `form-control ${meta.error && meta.touched ? 'is-invalid':''}`
    return (
      <div>
        <input {...input}
          className={className}
          placeholder={label}
          autoComplete="off" />
        {this.renderError(meta)}
      </div>
    );
  };

  onSubmit= (formValues) => {
    this.props.onSubmit(formValues);
  }

  render() {
    return (
      <form onSubmit={this.props.handleSubmit(this.onSubmit)}
        className="contact-form">
        <Field name="firstName"
          component={this.renderInput}
          label="First Name" />
        <Field name="lastName"
          component={this.renderInput}
          label="Last Name" />
        <Field name="email"
          component={this.renderInput}
          label="Email" />
        <button className="btn btn-lg btn-primary btn-block" type="submit">Submit</button>
      </form>
    )
  }
}

const validate = (formValues) => {
  const errors = {};

  if (!formValues.firstName) {
    errors.firstName = "You must enter first name";
  }
  if(!formValues.lastName) {
    errors.lastName = "You must enter last name";
  }
  if(!formValues.email) {
    errors.email = "You must enter an email";
  }

  return errors;
}

export default reduxForm({
  form: 'contactForm',
  validate: validate
})(ContactForm);
