import React from 'react';
import Christian from '../../images/team/Christian.jpg';
import Shane from '../../images/team/Shane.jpg'

const Team = ({height, width}) => {
  const titleStyle = {
    borderBottom: "2px solid #fff",
    maxWidth: "500px",
    margin: "auto",
    padding: "10px"
  }

  const handleImage = (name) => {
    return (
      <div className="col-4">
      <div className="m-4">
        <img src={name === 'shane' ? Shane : Christian} className="rounded mw-100" alt="Shane" />
      </div>
    </div>
    )
  }

  const handleContext = (name, context) => {
    return (
      <div className="col-4 align-self-center">
      <div className="h2">
        Shane Moody
        </div>
      <p className="sub-text">Born and raised in Denver, Colorado. Played college football at the University of Montana.
      Now a personal trainer at Elite Speed Performance in Denver.</p>
    </div>
    )
  }

  return (
    <>
      <div className="pt-6 pb-6" style={{ background: "#130f0a", opacity: ".98" }}>

        <h1 style={titleStyle}>Meet Your Host</h1>

        <div className="pt-5">
          <div className="row justify-content-center">
            {handleImage("shane")}
            {handleContext('shane', 'sdafasdfds')}
          </div>

          <div className="pt-5">
            <div className="row justify-content-center">
              <div className="col-4 align-self-center">
                <div className="h2">
                  Christian Backes
                </div>
                <p className="sub-text">Born and raised in Denver, Colorado. Played college football at the University of Northern Colorado.
              Now a personal trainer at Elite Speed Performance in Denver.</p>
              </div>
              <div className="col-4">
                <div className="m-4">
                  <img src={Christian} className="rounded mw-100" alt="Christian" />
                </div>
              </div>
            </div>
          </div>

        </div>

      </div>
    </>
  )
}

export default Team;
