import React from 'react';
import ChristianDrinking from '../images/team/ChristianDrinking.jpg';
import HeaderSection from './components/HeaderSection';
import Team from './components/Team';
import Beer from './components/beer';
import Platforms from './components/Platforms';

const Home = () => {
  const rowStyle = {
    width: "100%",
    maxWidth: "400px",
    maxHeight: "400px",
    position: "absolute",
    transform: "translate(-50%, -50%)",
    left: '50%',
    top: '50%',
    boxShadow: "0 0 1rem #fff",
    zIndex: "-1"
  }

  return (
    <div className="container-fluid" style={{maxWidth: "1400px", height: "100vh"}}>
    <div class="jumbotron jumbotron-fluid">
  <div class="container">
    <h1 class="display-4">Fluid jumbotron</h1>
    <p class="lead">This is a modified jumbotron that occupies the entire horizontal space of its parent.</p>
  </div>
</div>
      <img src={ChristianDrinking} class="rounded mx-auto d-block" alt="Shane" height="400px" width="400px" style={rowStyle}/>
    </div>
  )
}

export default Home;
