import React from 'react';
import ChristianDrinking from '../images/team/ChristianDrinking.jpg';
import HeaderSection from './components/HeaderSection';
import Team from './components/Team';
import Beer from './components/beer';
import Platforms from './components/Platforms';
import Anchor from '../images/platforms/anchor.jpg';

const Home = () => {
  const rowStyle = {
    position: "absolute",
    transform: "translate(-50%, -50%)",
    left: '50%',
    top: '50%',
    boxShadow: "0 0 1rem #fff",
    zIndex: "-1"
  }

  return (
    <>
    <div className="container-fluid" style={{maxWidth: "1400px", height: "100vh"}}>
      <img src={require(`../images/home/studio2.png`)} class="img-fluid rounded" alt="Responsive image" style={rowStyle}/>
    </div>
    <div className ="row">
      <div className="col text-center">
        <img src={Anchor} class="rounded" alt="..." />
      </div>
      <div className="col">
      <a title="Apple Inc. / Public domain" href="https://commons.wikimedia.org/wiki/File:Podcasts_(iOS).svg">
      <img width="256" alt="Podcasts (iOS)" src="https://upload.wikimedia.org/wikipedia/commons/thumb/e/e7/Podcasts_%28iOS%29.svg/256px-Podcasts_%28iOS%29.svg.png" />
      </a>
      </div>
      <div className="col">
        <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/9/96/YouTube_social_red_squircle_%282017%29.svg/1024px-YouTube_social_red_squircle_%282017%29.svg.png" class="rounded" alt="..." />
      </div>
    </div>
    </>
  )
}

export default Home;
