import React from 'react';
import CartSectionProduct from './CartSectionProduct';
const CartSection = ({cartTotal, cartProducts}) => {

  const products = cartProducts.map(p => {
    return (
      <CartSectionProduct cartProducts={cartProducts} />
    )
  });
  
  return(
    <>
    <h4 className="d-flex justify-content-between align-items-center mb-3">
        <span className="text-muted">Your Cart</span>
        <span className="badge badge-secondary badge-pill">{cartTotal.quantity}</span>
      </h4>

      <ul className="list-group mb-3">
      {products}
      </ul>
    </>
  )
}

export default CartSection;
