import React from 'react';
import { connect } from 'react-redux';
import ContactForm from './components/ContactForm';
import { contact } from '../../services/contact/actions';
import Mymodal from '../Modal';
import {Modal , Button} from 'react-bootstrap';
import history from '../../history';
import '../css/contact.css';

class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = { error: false };
  }

  renderCreate() {
    if(this.props.contacted){
        return (
        <Mymodal
        showValue={this.props.contacted}
        title="Thank you!"
        description="We look forward to meeting you. In the meantime, check out
        our store for some cool stuff like customized Beers&Brewskies Coasters!
        We will be in touch with you soon!"
        />
      )
    }

    if(this.state.error){
      const clientErrorDescription = `We look forward to meeting you. In the meantime, check out
      our store for some cool stuff like customized Beers&Brewskies Coasters!
      We will be in touch with you soon!`
      return (
            <Mymodal
            showValue={true}
            closeDirect = '/'
            buttonName = 'Close'
            title='Oops!'
            description= {clientErrorDescription}
            svgType="error"
            />
      );
    }
  }


  onSubmit = formValues => {
    this.props.contact(formValues)
      .then(res => console.log("We Will Talk With You Soon!"))
      .catch(err => this.setState({ error: true }) );
  }

  render() {
      return (
        <>
        {this.renderCreate()}
        <Modal show={true}
          onHide={() => history.push('/shop')}
          aria-labelledby="contained-modal-title-vcenter"
          centered
          >
          <Modal.Header closeButton>
            <Modal.Title>
              Contact Us
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <ContactForm onSubmit={this.onSubmit} />
          </Modal.Body>
        </Modal>
        </>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    contacted: state.contact.contacted,
    contactInfo: state.contact.contactInfo
  }
};


export default connect(mapStateToProps, { contact })(Contact);
