  componentDidMount() {
    this.props.fetchPodcasts();
  }

  render() {

    const { podcasts } = this.props;

    podcasts.forEach(p => {
      console.log(p.podcastSrc)
    });

    console.log(podcasts[1].podcastSrc)

  return (
    <>
      <h1>Hello</h1>
      <div className="media">
