import React from 'react';
import { connect } from 'react-redux';
import ContactForm from './forms/ContactForm';
import { contact } from '../actions';
import Modal from './Modal';

import './css/contact.css';

class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = { error: false };
  }

  renderCreate() {
    if(this.state.modal){
        return (
        <Modal
        showValue={this.state.modal}
        title="Thank you!"
        description="We look forward to meeting you. In the meantime, check out
        our store for some cool stuff like customized Beers&Brewskies Coasters!
        We will be in touch with you soon!"
        />
      )
    }
  }


  onSubmit = formValues => {
    this.props.contact(formValues)
      .then(res => console.log("Success"))
      .catch(err => this.onErrorCatch(err) )
  }

  onErrorCatch = (err) => {
    console.log("Looks Like We Are Having Issues. Please email us directly.");
    this.setState({ error: true });
  }

  render() {
    const rowStyle = {
      width: "70%",
    margin: "auto"
    };
    if(error){
      return (
        <div>Error</div>
      )
    }
      return (
        <div className="container">
          <div className="row" style={rowStyle}>
            <div className="col align-self-center form">
              <h3>Contact Us</h3>
              <ContactForm onSubmit={this.onSubmit} />
              {this.renderCreate()}
            </div>
          </div>
        </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    contacted: state.contact.contacted,
    contactInfo: state.contact.contactInfo
  }
};


export default connect(mapStateToProps, { contact })(Contact);
