import axios from 'axios';
import Stripe from 'stripe';

export const stripe = (products) => async (dispatch, getState) => {
  console.log(products);
  let response;
  // const response = await contacts.post('/contacts/add', {...formValues});
  try {
   response = await axios.post('/shop/checkout', products);
  } catch (e) {
  return console.log(e);
  }
  console.log(response.data.sessionId);

  const stripe = window.Stripe('pk_test_0kzCXvF6peItv0Z0FZ6QFG0j');
  console.log(stripe);
  stripe.redirectToCheckout({
  sessionId: response.data.sessionId
}).then(function (result) {
  // If `redirectToCheckout` fails due to a browser or network
  // error, display the localized error message to your customer
  // using `result.error.message`.
});


  // history.push('/contact?success');
};
