import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import CartProduct from '../floatcart/components/CartProduct';
import { updateCart } from '../../../../services/total/actions';
import { formatPrice } from '../util';
import { stripe } from '../../../../services/stripe/actions';

class Checkout extends React.Component {
  state = {
    cartTotal: this.props.cartTotal,
  };

  componentDidMount() {
    console.log("mounted")
    this.updateCart();
    console.log(this.props.cartTotal);
  }

  updateCart = () => {
    const cartProductsLs = JSON.parse(localStorage.getItem('cartProducts'));
    const cartTotalLs = JSON.parse(localStorage.getItem('cartTotal'));
    const { cartProducts, cartTotal } = this.props;
      if(cartProductsLs && cartTotalLs){
        if(cartProductsLs.length > 0){
          Object.assign(cartProducts, cartProductsLs);
          Object.assign(cartTotal, cartTotalLs);
        }
      }
    }

  proceedToPay = () => {

    const { cartTotal, cartProducts } = this.props;

    if (!cartTotal.productQuantity) {
      alert('Add some product in the cart!');
    } else {
      this.props.stripe(cartTotal, cartProducts);
    }

  };



    render(){
      const {cartTotal} = this.state;
      return (
        <div>{cartTotal.totalPrice}</div>
    )
  }
}

const mapStateToProps = state => {
  return {
  cartProducts: state.cart.products,
  cartTotal: state.total.data
  }
}

export default connect(mapStateToProps,{updateCart, stripe})(Checkout);
