import React from 'react';

const Confirmation = ({match}) => {
  return(
    <div className="container">
      <div className="row">
        <div className="container container--xs">
          <h1 className="mb-1 text-center">
            Thank You!
          </h1>
          <div className="fs-14 text-gray text-center mb-5">
            <p>
              Order ID: {match.params.id}
            </p>
            <p>We have sent you an email receipt. Thank you for shopping with us!</p>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Confirmation;
