import axios from 'axios';
import { isEmpty } from 'lodash';

export const insert_rating = async(rating, itemId) => {
    console.log(rating, itemId);
    return;
    if (isEmpty(rating)) return 'Rating is Empty';

    if (isEmpty(itemId)) return 'No Item Rating';

    let response;

    try {
        response = await axios.post('reviews/rating', {rating, itemId});
    }catch(e){
        console.log(e)
        return {err : 'Sorry, we are having issues submitting your rating'};
    }

    return {success: response.data};
}
