import React, { useState } from 'react';

const AddComment = () => {
    const [name, setName] = useState('');
    const [comment, setComment] = useState('');

    const handleFormSubmit = (e) => {
        e.preventDefault();

        console.log(name, comment);
    }

    return (
        <div className="modal d-flex" tabIndex="-1" role="dialog">
            <div className="modal-dialog modal-dialog-centered" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">Modal title</h5>
                        <button type="button" className="close" ariaLabel="Close">
                            <span ariaHidden="true">&times;</span>
                        </button>
                    </div>
                    <div className="modal-body">
                        <form onSubmit={handleFormSubmit}>
                            <input type="text" className="form-control" placeholder="Name" name="Name" onChange={(e) => setName(e.target.value)} value={name} />
                            <textarea type="text" className="form-control" placeholder="What do you think?" name="Comment" onChange={(e) => setComment(e.target.value)} value={comment} />
                            <button type="submit" className="btn btn-primary">Submit</button>
                        </form>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" >Close</button>
                        <button type="button" className="btn btn-primary">Save changes</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default AddComment;
