import React from 'react';
import {
  CardNumberElement,
  CardExpiryElement,
  CardCVCElement,
} from 'react-stripe-elements';

import {
  handleBlur,
  handleChange,
  handleClick,
  handleFocus,
  handleReady,
  createOptions
} from './utils';

class CardSection extends React.Component {
  constructor(props) {
  super(props);

  const paymentRequest = props.stripe.paymentRequest({
    country: 'US',
    currency: 'usd',
    total: {
      label: 'Demo total',
      amount: 1000,
    },
  });
}


  render() {
    return (
      <>
      <label>
  Name
  <input name="name" type="text" placeholder="Jane Doe" required />
</label>
<label>
  Email
  <input
    name="email"
    type="email"
    placeholder="jane.doe@example.com"
    required
  />
</label>
      <label>
        Card number
        <CardNumberElement
          onBlur={handleBlur}
          onChange={handleChange}
          onFocus={handleFocus}
          onReady={handleReady}
          {...createOptions(this.props.fontSize)}
        />
      </label>
      <label>
        Expiration date
        <CardExpiryElement
          onBlur={handleBlur}
          onChange={handleChange}
          onFocus={handleFocus}
          onReady={handleReady}
          {...createOptions(this.props.fontSize)}
        />
      </label>
      <label>
        CVC
        <CardCVCElement
          onBlur={handleBlur}
          onChange={handleChange}
          onFocus={handleFocus}
          onReady={handleReady}
          {...createOptions(this.props.fontSize)}
        />
      </label>
      </>
    );
  }
}

export default CardSection;
