import React from 'react';
import { connect } from 'react-redux';
import Podcast from './Podcast';

import {fetchPodcasts} from '../../../services/podcast/actions';

class PodcastList extends React.Component {

  componentDidMount() {
    this.props.fetchPodcasts();
  }

  render() {
    const titleStyle = {
      borderBottom: "2px solid #fff",
      maxWidth: "500px",
      margin: "auto",
      padding: "10px"
    }

    const { podcasts } = this.props;

    const podcastList = podcasts.map(p => {
      return <Podcast podcastSrc={p.podcastSrc} key={p._id}/>
    });


    podcastList.reverse();

  return (

    <>
    <div className="podcast-list pt-5 pb-5">
      <h1 className="mb-5" style={titleStyle}>Recent Episodes</h1>
      <iframe src="https://anchor.fm/books--brewskies/embed/episodes/Episode-8-Eric-Garcia-Locker-Room-Conversation-ebhu53/a-a1bvsum" height="102px" width="400px" frameborder="0" scrolling="no"></iframe>
      <iframe src="https://anchor.fm/books--brewskies/embed/episodes/Episode-7-Derrick-Middleton-Locker-Room-Conversation-ebbtj0/a-a1bvsum" height="102px" width="400px" frameborder="0" scrolling="no"></iframe>
      <iframe src="https://anchor.fm/books--brewskies/embed/episodes/Episode-6-Shaken-By-Tim-Tebow-Introduction-eb60ve/a-a1bvsum" height="102px" width="400px" frameborder="0" scrolling="no"></iframe>
      </div>
    </>
  )
}
}

const mapStateToProps = state =>({
  podcasts: state.podcasts
})

export default connect(mapStateToProps, {fetchPodcasts})(PodcastList);
