import axios from 'axios';
import { isEmpty } from 'lodash';

export const insert_rating = (rating, itemId) => {
    console.log(rating, itemId);

    if (isEmpty(rating)) return 'Rating is Empty';

    if (isEmpty(itemId)) return 'No Item Rating';

    let response;

    try {
        response = axios.post('reviews/rating', {rating, itemId});
    }catch(e){
        console.log(e)
        return 'Sorry, we are having issues submitting your rating';
    }

    console.log(response);

    return response;
}
