import { LOAD_CART,
  ADD_PRODUCT,
  REMOVE_PRODUCT,
  CHANGE_PRODUCT_QUANTITY
} from '../actions/actionTypes';

import _ from 'lodash';


const initialState = {
  products: [],
};

export default (state = initialState, action) => {
  switch (action.type) {
    case LOAD_CART:
      return {
        ...state,
        products: action.payload
      };
    case ADD_PRODUCT:
      return {
        ...state,
        products: [...state.products, action.payload]
      };
    case REMOVE_PRODUCT:
      return {
        ...state,
        products: [...state.products]
      };
    case CHANGE_PRODUCT_QUANTITY:
      // const index = action.payload.i;
      // const newProduct = update(state, {
      //   products: {
      //         [index]: {$set: action.payload.product}
      //   }
      // });

      _.set(state, 'products[0]','dsdsaf');
      console.log(state);


      return {
        ...state,
        products: [...state.products]
      };
    default:
      return state;
  }
}
