import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import CartProduct from '../floatcart/components/CartProduct';
import { updateCart } from '../../../../services/total/actions';
import { formatPrice } from '../util';
import { stripe } from '../../../../services/stripe/actions';

class Checkout extends React.Component {
  componentDidUpdate() {
    this.updateLocalStorage();
  }

  componentDidMount() {
    this.updateCart();
  }

  updateLocalStorage = () => {
    const { cartProducts, cartTotal } = this.props;
    localStorage.setItem('cartProducts', JSON.stringify(cartProducts));
    localStorage.setItem('cartTotal', JSON.stringify(cartTotal));
  }

  updateCart = () => {
    const cartProductsLs = JSON.parse(localStorage.getItem('cartProducts'));
    const cartTotalLs = JSON.parse(localStorage.getItem('cartTotal'));
    const { cartProducts, cartTotal } = this.props;
      if(cartProductsLs && cartTotalLs){
        if(cartProductsLs.length > 0){
          Object.assign(cartProducts, cartProductsLs);
          Object.assign(cartTotal, cartTotalLs);
        }
      }
    }


  removeProduct = product => {
    const { cartProducts, updateCart } = this.props;

    const index = cartProducts.findIndex(p => p.id === product.id);
    if (index >= 0) {
      cartProducts.splice(index, 1);
      updateCart(cartProducts);
    }
  };

  changeProductQuantity = changedProduct => {
    const { cartProducts, updateCart } = this.props;

    const product = cartProducts.find(p => p.id === changedProduct.id);
    product.quantity = changedProduct.quantity;
    if (product.quantity <= 0) {
      this.removeProduct(product);
    }
    updateCart(cartProducts);
  }

  proceedToPay = () => {

    const { cartTotal, cartProducts } = this.props;

    if (!cartTotal.productQuantity) {
      alert('Add some product in the cart!');
    } else {
      this.props.stripe(cartTotal, cartProducts);
    }

  };



    render(){
      const { cartProducts, cartTotal } = this.props;
      const { removeProduct, changeProductQuantity } = this

      const products = cartProducts.map(p => {
        return (
          <CartProduct product={p} removeProduct={removeProduct} changeProductQuantity={changeProductQuantity} key={p.id} />
        );
      });


      return (
      <React.Fragment>
        {products}
        <p className="sub-price__val">
          {`${cartTotal.currencyFormat} ${formatPrice(
            cartTotal.totalPrice,
            cartTotal.currencyId
          )}`}
        </p>
        <div onClick={() => this.proceedToPay()} className="buy-btn">
          Checkout
        </div>
      </React.Fragment>
    )
  }
}

const mapStateToProps = state => {
  return {
  cartProducts: state.cart.products,
  cartTotal: state.total.data
  }
}

export default connect(mapStateToProps,{updateCart, stripe})(Checkout);
