import React, {useRef} from 'react';
import Team from './components/Team';
import PodcastList from './components/PodcastList';

const scrollToRef = (ref) => window.scrollTo(0, ref.current.offsetTop, "smooth") ;

const Home = () => {
  const myRef = useRef(null);

  const rowStyle = {
    position: "absolute",
    transform: "translate(-50%, -50%)",
    left: '50%',
    top: '50%',
    boxShadow: "0 0 1rem #fff",
    zIndex: "-1"
  }

  const handleListenClick = () => {
    let podcastList = document.getElementById("podcast-list");
    podcastList.scrollIntoView();
  }

  return (
    <>
    <div className="container-fluid home-header">
    <div className="row align-items-center">
    <div className="col align-self-center">
      <h1 className="display-4 font-weight-normal">Books & Brewskies</h1>
      <p className="lead font-weight-normal">Created by the men, for the boys.</p>
      <button className="btn btn-primary" onClick={()=>scrollToRef(myRef)}>Listen</button>
    </div>
    </div>
    </div>
    <div className="container home-context rounded">
        <Team />
      <div ref={myRef} id="podcast-list">
        <PodcastList ref={myRef} />
        </div>
    </div>
    </>
  )
}

export default Home;
