import React from 'react';
import { connect } from 'react-redux';
import ContactForm from './forms/ContactForm';
import { contact } from '../actions';
import Modal from './Modal';

import './css/contact.css';

class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = { error: false,
      contacted: false,
      contactInfo: {
        firstName:"First Name",
        lastName:"Last Name",
        email:"email",
        message: "Say Something Damn it!"
      }
    };
  }

  renderCreate() {
    if(this.props.contact.contacted){
        return (
        <Modal
        showValue={this.props.contact.contacted}
        title="Thank you!"
        description="We look forward to meeting you. In the meantime, check out
        our store for some cool stuff like customized Beers&Brewskies Coasters!
        We will be in touch with you soon!"
        />
      )
    }
  }


  onSubmit = formValues => {
    this.props.contact(formValues)
      .then(res => console.log("We Will Talk With You Soon!"))
      .catch(err => this.setState({ error: true }) );
  }

  render() {
    const rowStyle = {
      width: "70%",
    margin: "auto"
    };
    // if(this.state.error){
    //     return (
    //       <Modal
    //       showValue= {this.state.error}
    //       title="Oops!"
    //       description="Sorry, it looks like we are having issues. Please email us directly. We look forward to
    //       meeting you! Please feel free to look around."
    //       />
    //     )
    // }
        console.log(this.state.contacted);
      return (
        <div className="container">
          <div className="row" style={rowStyle}>
            <div className="col align-self-center form">
              <h3>Contact Us</h3>
              <ContactForm onSubmit={this.onSubmit} />
              {this.renderCreate()}
            </div>
          </div>
        </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    contacted: state.contacted,
    contactInfo: state.contactInfo
  }
};


export default connect(mapStateToProps, { contact })(Contact);
