import { UPDATE_CART_TOTAL } from '../actions/actionTypes';

const initialState = {
  cartTotal: 0,
};


export default (state = initialState, action) => {
  switch (action.type) {
    case UPDATE_CART_TOTAL:
      return {
        ...state,
        cartTotal: action.payload
      };
    default:
      return state;
  }
}
