  const titleStyle = {
    maxWidth: "500px",
    margin: "auto",
    padding: "10px",
    fontSize: '3.5rem'
  }

  let imgStyle = {
    position: "relative",
    bottom: '170px'
};

if(scrollHeight > .03 && scrollHeight < .34){
console.log(scrollHeight)
imgStyle = {
    position: "relative",
    bottom: `${170 - (scrollHeight * 500)}px`
}
}else if(scrollHeight > .34){
imgStyle = {
    position: "relative",
    bottom: "0"
}
}


  return (
    <>
    <div id="chevron-attraction" />
    <div className="pt-6 pb-6 text-center special-events" style={{color: '#332212', backgroundColor: '#fff'}}>
      <div className="row mt-6 m-auto">
        <div className="col">
        <div class="col-md-4"><img className="img-fluid" src={Book} /></div>
    <div class="col-md-4 offset-md-4"></div>
          {/* <img className="mt-3" src={Book} alt ="book" style={imgStyle} /> */}
          {/* <h2 className="botmw-text-design">Book Of The Month</h2>
          <h3 className="mt-3">The Art of War</h3>
          <p className="mt-2">By: Sun Tzu</p> */}
        </div>
      </div>
      <div className="row m-auto">
      <div class="col">Beer Of The Week</div>
    <div class="col"><img className="img-fluid" src={Book} /></div>
          {/* <img className="mt-3" src={Beer} alt="beer" style={imgStyle} />
          <h2 className="botmw-text-design">Beer Of The Week</h2>
          <h3 className="mt-3">Coors Light</h3>
          <p className="mt-2">From: Golden, Colorado</p> */}
      </div>
    </div>
    </>
  )
}

export default CoolStuff;
