import React, { useState } from 'react';

const AddComment = () => {
    const [name, setName] = useState('');
    const [comment, setComment] = useState('');

    const handleFormSubmit = (e) => {
        e.preventDefault();

        console.log(name, comment);
    }

    return (
        <div className="modal" tabIndex="-1" role="dialog">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                <form onSubmit={handleFormSubmit}>
                    <input type="text" className="form-control" placeholder="Name" name="Name" onChange={(e) => setName(e.target.value)} value={name} />
                    <textarea type="text" className="form-control" placeholder="What do you think?" name="Comment" onChange={(e) => setComment(e.target.value)} value={comment} />
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
                </div>
            </div>
        </div>
    )
}

export default AddComment;
