import React, {useState} from 'react';
import Together from '../../images/home/together.jpg';
import ShaneBack from '../../images/home/shaneback.jpg';

const Attraction = ({scrollHeight}) => {

    const [bottomHeight, setBottomHeight] = useState(170)

    let imgStyle = {
            position: "relative",
            bottom: '170px'
    };

    if(scrollHeight > 170){
        let newBottomHeight = bottomHeight - 1
        setBottomHeight(newBottomHeight)
        console.log(bottomHeight)
        imgStyle = {
            position: "relative",
            bottom: `${bottomHeight}px`
        }
    }

    if (scrollHeight > 800) {
        imgStyle = {
            position: 'relative',
            bottom: '0'
        }
    }

    return (
        <>
        <div id="chevron-attraction" />
        <div className="pt-6 pb-6" id="store" style={{color: '#332212', backgroundColor: '#fff'}}>
            <div className="row m-auto">
            <div className="col">
            <img src={ShaneBack} className="rounded" style={imgStyle}/>
            </div>
            <div className="col">
                <h1>Best Podcast In Town!</h1>
            </div>
            </div>
        </div>
        </>
    )
}

export default Attraction;
