import React from 'react';
import {injectStripe} from 'react-stripe-elements';
import CardSection from './CardSection';

class CheckoutForm extends React.Component {
  handleSubmit = (ev) => {
     ev.preventDefault();
     if (this.props.stripe) {
       this.props.stripe
         .createToken()
         .then((payload) => console.log('[token]', payload));
     } else {
       console.log("Stripe.js hasn't loaded yet.");
     }
   };
   render() {
     return (
       <form onSubmit={this.handleSubmit}>
         <label>
           Card number
           <CardNumberElement
             onBlur={handleBlur}
             onChange={handleChange}
             onFocus={handleFocus}
             onReady={handleReady}
             {...createOptions(this.props.fontSize)}
           />
         </label>
         <label>
           Expiration date
           <CardExpiryElement
             onBlur={handleBlur}
             onChange={handleChange}
             onFocus={handleFocus}
             onReady={handleReady}
             {...createOptions(this.props.fontSize)}
           />
         </label>
         <label>
           CVC
           <CardCVCElement
             onBlur={handleBlur}
             onChange={handleChange}
             onFocus={handleFocus}
             onReady={handleReady}
             {...createOptions(this.props.fontSize)}
           />
         </label>
         <button>Pay</button>
       </form>
     );
   }
}


export default CheckoutForm;
