import React, { Component } from 'react';
import PropTypes from 'prop-types';

import { connect } from 'react-redux';
import { removeProduct, changeProductQuantity } from '../../../../../services/cart/actions';
// import { updateCart } from '../../../../../services/total/actions';
import Product from './Product';

class ProductList extends Component {

  render() {
    const { cartTotal, cartProducts, removeProduct, changeProductQuantity } = this.props;

    const products = cartProducts.map(p => {
      return (
        <Product product={p} removeProduct={removeProduct} changeProductQuantity={changeProductQuantity} key={p.id} />
      );
    });

    return (
      <div className="float-cart__shelf-container">
        {products}
        {!products.length && (
          <p className="shelf-empty">
            Add some products in the cart <br />
            :)
          </p>
        )}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  cartProducts: state.cart.products,
  cartTotal: state.total.data
});

export default connect(
  mapStateToProps,
  { removeProduct, changeProductQuantity }
)(ProductList);
