import React from 'react';
import { CardElement } from 'react-stripe-elements';

import {
  handleBlur,
  handleChange,
  handleClick,
  handleFocus,
  handleReady,
  createOptions
} from './utils';

import './css/style.css';

class CardSection extends React.Component {

  render() {
    return (
      <>
      <div className="row">
      <div className="col">
      <label>
        Name
        <input name="name" type="text" placeholder="Jane Doe" required />
        </label>
      </div>
      <div className="col">
      <label>
        Email
        <input
          name="email"
          type="email"
          placeholder="jane.doe@example.com"
          required
        />
        </label>
        </div>
        </div>

      <div className="row">
      <label>
        Card details
        <CardElement
          onBlur={handleBlur}
          onChange={handleChange}
          onFocus={handleFocus}
          onReady={handleReady}
          {...createOptions(this.props.fontSize)}
        />
      </label>
      </div>
      </>
    );
  }
}

export default CardSection;
