import React from 'react';
import { isEmpty } from 'lodash';
import axios from 'axios';

class BB extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            reviewItems:[]
        }
    }
    componentDidMount = async() => {
        const reviewItems = await axios.get('/reviews');
        console.log(reviewItems);
        this.setState({reviewItems: reviewItems.data});
    }

    render() {
        if(isEmpty(this.state.reviewItems)){
            return (
                <div>Empty</div>
            )
        }

        const review = this.state.reviewItems.map((item, index) => {
            return (
                <img src={item.imageUrl} alt={item.name} key={index}/>
            )
        })

        return(
            <div>
                {review}
            </div>
        )
    }
}

export default BB;
