import { UPDATE_CART_TOTAL } from '../actions/actionTypes';

const initialState = {
  cartTotal: {
    productQuantity: 0,
    installments: 0,
    totalPrice: 0,
    currencyId: 'USD',
    currencyFormat: '$'
  }
};

export default function(state = initialState, action) {
  switch (action.type) {
    case UPDATE_CART:
      return {
        ...state,
        cartTotal: action.payload
      };
    default:
      return state;
  }
}
