import React from 'react';
import { isEmpty } from 'lodash';
import axios from 'axios';
import Modal from '../Modal';

class BB extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            reviewItems: [],
            error: false
        }
    }
    componentDidMount = async() => {
        let reviewItems;

        try {
            reviewItems = await axios.get('/reviews');
        }catch(e){
            console.log(e)
            return this.setState({error: true})
        }

        this.setState({reviewItems: []});

    }

    render() {
        if(this.state.error){
            return (
                <Modal
                showValue={true}
                closeDirect='/'
                buttonName='Close'
                title='Error'
                description='Sorry, we are undergoing maintenance. Check back again later.'
                svgType="error"
              />
            )
        }

        if(isEmpty(this.state.reviewItems)){
            return (
                <div>Empty</div>
            )
        }

        const review = this.state.reviewItems.map((item, index) => {
            return (
                <img src={item.imageUrl} alt={item.name} key={index}/>
            )
        })

        return(
            <div>
                {review}
            </div>
        )
    }
}

export default BB;
