import React, { useState } from 'react';
import { isEmpty } from 'lodash';
import StarRating from './StarRating';
import { insert_comment } from '../../../services/bb/actions';
import Modal from '../../Modal';

const Item = ({name, id, comments, image, custAvg, custStar, myAvg, myStar, myComment, handleClose}) => {
    const [formName, setFormName] = useState('');
    const [formComment, setFormComment] = useState('');
    const [error, setError] = useState(false);
    const [commentSuccessObj, setCommentSuccessObj] = useState({});

    const handleFormSubmit = (e) => {
        e.preventDefault();

        insert_comment(formName, formComment, id)
            .then((obj) => {
                setCommentSuccessObj({name: obj.name, comment: obj.comment});
            })
            .catch((err) => {
                console.log(err);
                setError(true);
            });
    }

    
    if(!isEmpty(commentSuccessObj)) {
        comments.push(commentSuccessObj);
        setCommentSuccessObj({});
    }

    let mapComments = [];

    if(!isEmpty(comments)){
        mapComments = comments.map((item, index) => {
            return (
                <div className="media" key={index}>
                    <img src="./media/logo.png" className="mr-3" alt="BB" />
                    <div className="media-body">
                        <p className="mt-0 mb-0" style={{fontSize: "1rem"}}>{item.name}</p>
                        <p className="text-muted">{item.comment}</p>
                    </div>
                </div>
            )
        });               
    }

    if (error) {
        return (
            <Modal
                showValue={true}
                closeDirect='/bb'
                buttonName='Back'
                handleState={() => {
                    setError(false);
                    handleClose()
                }}
                title='Error'
                description='Sorry, something went wrong'
                svgType="error"
            />
        )
    }

    return (
        <div className="modal d-flex" tabIndex="1" role="dialog" style={{overflow: 'auto'}}>
            <div className="modal-dialog w-100" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <div className="row m-auto" style={{fontSize: '.6rem'}}>
                            <div className="col">
                                <h5 className="modal-title">{name}</h5>
                                <div className="d-flex">Our Rating: {myStar}{myAvg}</div>
                                <div className="d-flex">Everyone's Rating: {custStar}{custAvg}</div>
                                <div className="d-flex">Our Comment:<p className="text-muted ml-1">{myComment}</p></div>
                                </div>
                                <div className="col text-right"><img src={image} className="img-fluid rounded shadow" alt="item image" /></div>
                        </div>
                        <button type="button" className="close" aria-label="Close" onClick={handleClose}>
                            <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <form onSubmit={handleFormSubmit}>
                        <div className="modal-body">
                            {(!isEmpty(mapComments)) ? mapComments : <p>No Comments</p>}
                            <div className="form-group">
                                <input type="text"
                                    className="form-control"
                                    placeholder="Name" name="Name"
                                    onChange={(e) => setFormName(e.target.value)}
                                    value={formName}
                                    required />
                            </div>
                            <div className="form-group">
                                <textarea type="text"
                                    className="form-control"
                                    maxLength='500'
                                    placeholder="What do you think?"
                                    name="Comment"
                                    onChange={(e) => setFormComment(e.target.value)}
                                    value={formComment} style={{ minHeight: '150px' }}
                                    required />
                            </div>
                            <p className="m-0">Rating</p>
                            <StarRating itemId={id} setError={() => setError(true)} />
                        </div>
                        <div className="modal-footer">
                            <button type="submit" className="btn btn-primary btn-block">Submit</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default Item;
