import React from 'react';
import Thumb from './Thumb';
import { connect } from 'react-redux';
import { addProduct, changeProductQuantity } from '../../../actions';

class Product extends React.Component {

  checkForDuplicate(product) {
    var duplicate = false;

    this.props.cartProducts.forEach((item, i) => {
      if(item.id === product.id){
        item.quantity = product.quantity + 1;
        this.props.changeProductQuantity()
        duplicate = true;
      }
    });

    if(!duplicate){
      product.quantity = 0;
      this.props.addProduct(product);
    }
  }

  render() {
    const formatPrice = (x, currency) => {
          return x.toFixed(2);
    };

    const { product } = this.props;

    return (

      <div
        className="shelf-item"
        onClick={() => this.checkForDuplicate(product)}
        data-sku={product.sku}
      >
        <Thumb
          classes="shelf-item__thumb"
          src={require(`../static/products/${product.sku}_1.jpg`)}
          alt={product.title}
        />
        <p className="shelf-item__title">{product.title}</p>
        <div className="shelf-item__price">
          <div className="val">
            <small>{product.currencyFormat}</small>
            <b>{formatPrice(product.price,product.currencyFormat)}</b>
            <span></span>
          </div>
        </div>
        <div className="shelf-item__buy-btn">Add to cart</div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    cartProducts: state.cart.products,
    newProduct: state.cart.productToAdd,
    productToRemove: state.cart.productToRemove,
    productToChange: state.cart.productToChange,
  }
};

export default connect(
  mapStateToProps,
  { addProduct, changeProductQuantity }
)(Product);
