import React, { Component } from 'react';
import PropTypes from 'prop-types';

import { connect } from 'react-redux';
import { removeProduct, changeProductQuantity } from '../../../../../services/cart/actions';
// import { updateCart } from '../../../../../services/total/actions';
import CartProduct from '../../floatcart/components/CartProduct';

class ProductList extends Component {

  render() {
    const { cartTotal, cartProducts, removeProduct, changeProductQuantity } = this.props;

    const products = cartProducts.map(p => {
      return (
        <CartProduct product={p} removeProduct={removeProduct} changeProductQuantity={changeProductQuantity} key={p.id} />
      );
    });

    return (
      <div>
      This is the list of products
      {products}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  cartProducts: state.cart.products,
  cartTotal: state.total.data
});

export default connect(
  mapStateToProps,
  { removeProduct, changeProductQuantity }
)(ProductList);
