import React from 'react';
import { connect } from 'react-redux';
import {
  loadCart,
  removeProduct,
  changeProductQuantity
} from '../../../actions';
import CartProduct from './CartProduct';

import './style.scss';

class FloatCart extends React.Component {
  state = {
    isOpen: false
  };

  componentWillReceiveProps(prevProps){
    if (prevProps.newProduct !== this.props.newProduct) {
      this.addProduct(this.props.newProduct);
    }

    if (prevProps.productToRemove !== this.props.productToRemove) {
      this.removeProduct(this.props.newProduct);
    }

    if (prevProps.productToChange !== this.props.productToChange) {
      this.changeProductQuantity(this.props.newProduct);
    }

  }

  openFloatCart = () => {
    this.setState({ isOpen: true });
  };

  closeFloatCart = () => {
    this.setState({ isOpen: false });
  };

  addProduct = (product) => {
    const { cartProducts} = this.props;
    let productAlreadyInCart = false;

    cartProducts.forEach(cp => {
      if (cp.id === product.id) {
        cp.quantity += product.quantity;
        productAlreadyInCart = true;
      }
    });

    if (!productAlreadyInCart) {
      cartProducts.push(product);
    }

  }

  removeProduct = (product) => {
    const { cartProducts } = this.props;

    const index = cartProducts.findIndex(p => p.id === product.id);
    if (index >= 0) {
      cartProducts.splice(index, 1);
    }

  };

  changeProductQuantity = (changedProduct) => {
    const { cartProducts } = this.props;

    const product = cartProducts.find(p => p.id === changedProduct.id);
    product.quantity = changedProduct.quantity;
    if (product.quantity <= 0) {
      this.removeProduct(product);
    }
  }

  render(){
    const { cartProducts, removeProduct, changeProductQuantity } = this.props;

    const products = cartProducts.map(p => {
      return (
        <CartProduct product={p} removeProduct={removeProduct} changeProductQuantity={changeProductQuantity} key={p.id} />
      );
    });

    let classes = ['float-cart'];

    if (!!this.state.isOpen) {
      classes.push('float-cart--open');
    }

    return (
      <div className={classes.join(' ')}>
        {/* If cart open, show close (x) button */}
        {this.state.isOpen && (
          <div
            onClick={() => this.closeFloatCart()}
            className="float-cart__close-btn"
          >
            X
          </div>
        )}

        {/* If cart is closed, show bag with quantity of product and open cart action */}
        {!this.state.isOpen && (
          <span
            onClick={() => this.openFloatCart()}
            className="bag bag--float-cart-closed"
          >
            <span className="bag__quantity"></span>
          </span>
        )}

        <div className="float-cart__content">
          <div className="float-cart__header">
            <span className="bag">
              <span className="bag__quantity"></span>
            </span>
            <span className="header-title">Cart</span>
          </div>

          <div className="float-cart__shelf-container">
            {products}
            {!products.length && (
              <p className="shelf-empty">
                Add some products in the cart <br />
                :)
              </p>
            )}
          </div>
        </div>
      </div>
    );

  }
}

const mapStateToProps = state => {
  return {
    cartProducts: state.cart.products,
    newProduct: state.cart.productToAdd,
    productToRemove: state.cart.productToRemove,
    productToChange: state.cart.productToChange,
  }
};


export default connect(
  mapStateToProps,
  {loadCart, removeProduct, changeProductQuantity})
  (FloatCart);
