  // }
  //
  // componentDidMount() {
  //
  // }
  //
  // componentDidUpdate() {
  //     return <Modal showValue={this.state.modal} />;
  // }


  onSubmit = formValues => {
    this.props.contact(formValues);
  }

  render() {
    if(this.props.contacted){
      <Modal showValue={this.props.contacted} />
    }

      return (
        <div>
          <h3>Contact Us</h3>
          <ContactForm onSubmit={this.onSubmit} />
        </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    contacted: state.contact.contacted,
    contactInfo: state.contact.contactInfo
  }
};


export default connect(mapStateToProps, { contact })(Contact);
