import React from 'react';
import {ElementsConsumer, CardElement} from '@stripe/react-stripe-js';
import { connect } from 'react-redux';
import CardSection from './components/CardSection';
import IdentitySection from './components/IdentitySection';
import AddressSection from './components/AddressSection';
import './css/CheckoutFormStyles.css';
import Spinner from '../../../spinner'
import CartSection from './components/CartSection';
import { getLocalStorageUpdateCart } from '../util';
import { getStripe, storeOrder } from '../../../../services/order/actions';
import { clearCart } from '../../../../services/cart/actions';
import { clearCartTotal } from '../../../../services/total/actions';
import history from '../../../../history';
import Modal from '../../../Modal';

class CheckoutForm extends React.Component {

  state = {
    name: '',
    email: '',
    phone: '',
    line1: '',
    city: '',
    state: '',
    zip: '',
    client_secret: '',
    client_error: false,
    order_completed: false,
    orderId: '',
    buttonLoading: false
  }

  componentDidMount = async() => {
    const {cartTotal, cartProducts} = this.props;
    getLocalStorageUpdateCart(cartProducts, cartTotal);
    if(cartProducts.length > 0 ){
      const response = await getStripe(cartTotal, cartProducts);

      if(response.error){
        this.setState({client_error: response.error});
      }else if (response.data){
        this.setState({client_secret: response.data});
      }

    }
  }

  handleSubmit = async (event) => {
    // We don't want to let default form submission happen here,
    // which would refresh the page.
    event.preventDefault();

    const {stripe, elements, cartProducts, cartTotal} = this.props
    const {name, phone, email, line1, city, state, zip, client_secret} = this.state;

    if (!stripe || !elements) {
      // Stripe.js has not yet loaded.
      // Make  sure to disable form submission until Stripe.js has loaded.
      return;
    }

    const result = await stripe.confirmCardPayment(client_secret, {
      payment_method: {
        card: elements.getElement(CardElement),
        billing_details: {
          name: name,
          phone: phone,
          email: email
        },
      },
      shipping: {
        name: name,
        address: {
          line1: line1,
          city: city,
          state: state,
          postal_code: zip
        }
      }
    });

    if (result.error) {
      // Show error to your customer (e.g., insufficient funds)
      console.log(result.error.message);
    } else {
      // The payment has been processed!
      if (result.paymentIntent.status === 'succeeded') {
        // Show a success message to your customer
        // There's a risk of the customer closing the window before callback
        // execution. Set up a webhook or plugin to listen for the
        // payment_intent.succeeded event that handles any business critical
        // post-payment actions.

        // const storeOrder = await axios.post('/shop/checkout/success');
        // storeOrder
        //   .then()
        //   .catch()

        const response = await storeOrder(
          result.paymentIntent,
          cartProducts,
          cartTotal,
          name,
          phone,
          email);

          if(response.error){
            this.setState({client_error: response.error});
          }else if(response.data){
            localStorage.removeItem('cartTotal');
            localStorage.removeItem('cartProducts');
            this.props.clearCart();
            this.props.clearCartTotal();
            this.setState({ order_completed: true, orderId: response.data.chargeId });
            // history.push(`confirmation/${response.data.chargeId}`);
          }

      }
    }
  };

  handleInputChange = (event) => {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  render() {
    const { name, phone, email, line1, city, state, zip, client_secret, client_error, order_completed, orderId,buttonLoading } = this.state;
    const { cartTotal, cartProducts } = this.props;

    if(order_completed){
      const description = `Your confirmation number is ${orderId}.
      Check your email for details. If you have any questions or
      concerns regarding your order please feel free to contact us.`;

      return(
        <Modal
        showValue={true}
        closeDirect = '/'
        buttonName = 'OK'
        title={`Thank You ${name}!`}
        description= {description}
        />
      )
    }

    if(cartProducts.length <= 0) {
      return <h1>Cart is empty</h1>;
    }

    if(client_secret === ''){
      if(client_error){
        return <h1>We have a problem :(</h1>;
      }else{
        return <Spinner />;
      }
    }

    return (
      <div className="row">
      <div className="col-md-4 order-md-2 mb-4">
        <CartSection cartTotal={cartTotal} cartProducts={cartProducts} />
      </div>
      <div className="col-md-8 order-md-1">
        <form onSubmit={this.handleSubmit}>
          <IdentitySection name={name} phone={phone} email={email} handleInputChange={this.handleInputChange}/>
          <AddressSection line1={line1} city={city} state={state} zip={zip} handleInputChange={this.handleInputChange} />
          <CardSection />
            <button className="btn btn-primary" disabled={!this.props.stripe} onClick={() => this.setState({buttonLoading: true})}>
              {buttonLoading && <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span> }
              Confirm
            </button>
        </form>
      </div>
    </div>
    );
  }
}

const InjectedCheckoutForm = ({cartProducts, cartTotal, clearCart, clearCartTotal}) => {
  return (
    <ElementsConsumer>
      {({stripe, elements}) => (
        <CheckoutForm  stripe={stripe}
          elements={elements}
          cartProducts={cartProducts}
          cartTotal={cartTotal}
          clearCart={clearCart}
          clearCartTotal={clearCartTotal}
          />
      )}
    </ElementsConsumer>
  );

};

const mapStateToProps = (state) => ({
  cartTotal: state.total.data,
  cartProducts: state.cart.products
});

export default connect(mapStateToProps, {clearCart, clearCartTotal})(InjectedCheckoutForm);
