import React from 'react';
import { connect } from 'react-redux';
import ContactForm from './forms/ContactForm';
import { contact } from '../actions';
import Modal from './Modal';
import {Button} from 'react-bootstrap';

import './css/Contact.css';

class Contact extends React.Component {
  // constructor(props) {
  //   super(props);
  //   this.state = {modal: true};
  // }
  //
  // componentDidMount() {
  //
  // }
  //
  // componentDidUpdate() {
  //     return <Modal showValue={this.state.modal} />;
  // }

  renderCreate() {
    if(this.props.contacted){
        return (
        <Modal
        showValue={this.props.contacted}
        title="Thank You"
        description="We will be in touch"
        />
      )
    }
  }


  onSubmit = formValues => {
    this.props.contact(formValues);
  }

  render() {
      return (
        <div>
          <h3>Contact Us</h3>
          <ContactForm onSubmit={this.onSubmit} />
          {this.renderCreate()}
        </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    contacted: state.contact.contacted,
    contactInfo: state.contact.contactInfo
  }
};


export default connect(mapStateToProps, { contact })(Contact);
