import React from 'react';
import './css/CardSectionStyles.css';

const AddressSection = ({line1, city, state, zip, handleInputChange}) => {
  const CARD_ELEMENT_OPTIONS = {
    style: {
      base: {
        color: "#32325d",
        fontFamily: '"Helvetica Neue", Helvetica, sans-serif',
        fontSmoothing: "antialiased",
        fontSize: "16px",
        "::placeholder": {
          color: "#aab7c4",
        },
      },
      invalid: {
        color: "#fa755a",
        iconColor: "#fa755a",
      },
    },
  };

  return(
    <>
    <div className="row">
    <input
    name="line1"
    placeholder="Address"
    required
    value={line1}
    onChange={handleInputChange}
    options={CARD_ELEMENT_OPTIONS}
    />
    </div>
    <div className="row">
    <input
    name="city"
    placeholder="City"
    required
    value={city}
    onChange={handleInputChange}
    />
    <input
    name="state"
    placeholder="State"
    required
    value={state}
    onChange={handleInputChange}
    />
    <input
    name="zip"
    placeholder="Zip"
    required
    value={zip}
    onChange={handleInputChange}
    />
    </div>
    </>
  )
}

export default AddressSection;
