import React from 'react';
import { Field, reduxForm } from 'redux-form';

class ContactForm extends React.Component {
  renderError({ error, touched }) {
    if (touched && error) {
      return (
        <div className="invalid-feedback" style={{marginBottom: ".25rem"}}>
          {error}
        </div>
      )
    }
  }

  renderInput = ({ input, label, meta }) => {
    const className = `form-control ${meta.error && meta.touched ? 'is-invalid':''}`
    const type = label === 'Email' ? 'email': 'text';
    return (
      <div>
        <input {...input}
          className={className}
          placeholder={label}
          autoComplete="off"
          type = {type}
          />
        {this.renderError(meta)}
      </div>
    );
  };

  onSubmit= (formValues) => {
    this.props.onSubmit(formValues);
  }

  render() {
    return (
      <form onSubmit={this.props.handleSubmit(this.onSubmit)}
        className="contact-form">
        <Field name="firstName"
          component={this.renderInput}
          label="First Name" />
        <Field name="lastName"
          component={this.renderInput}
          label="Last Name" />
        <Field name="email"
          component={this.renderInput}
          label="Email"
          />
        <button className="btn btn-lg btn-primary btn-block" type="submit" data-toggle="modal" data-target="#exampleModalCenter">Submit</button>
              <div className="modal fade" id="exampleModalCenter" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
          <div className="modal-dialog modal-dialog-centered" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalCenterTitle">Modal title</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                ...
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                <button type="button" className="btn btn-primary">Save changes</button>
              </div>
            </div>
          </div>
        </div>
      </form>
    )
  }
}

const validate = (formValues) => {
  const errors = {};

  if (!formValues.firstName) {
    errors.firstName = "Invalid First Name";
  }
  if(!formValues.lastName) {
    errors.lastName = "Invalid Last Name";
  }
  if(!formValues.email) {
    errors.email = "Invalid Email";
  }

  return errors;
}

export default reduxForm({
  form: 'contactForm',
  validate: validate
})(ContactForm);
