import React from 'react';
import { connect } from 'react-redux';
import ContactForm from './components/ContactForm';
import { contact } from '../../services/contact/actions';
import Mymodal from '../Modal';
import {Modal , Button} from 'react-bootstrap';
import history from '../../history';

class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: false,
      loading: false,
      success: false
    };
  }

  onSubmit = formValues => {
    this.setState({loading: true});

    this.props.contact(formValues)
      .then(res => console.log("We Will Talk With You Soon!"))
      .catch(err => this.setState({ error: true }) );
  }

  render() {
    const {error, success, loading} = this.state;
    if(error){
      const clientErrorDescription = `We look forward to meeting you. In the meantime, check out
      our store for some cool stuff like customized Beers&Brewskies Coasters!
      We will be in touch with you soon!`
      return (
            <Mymodal
            showValue={true}
            closeDirect = '/'
            buttonName = 'Close'
            title='Oops!'
            description= {clientErrorDescription}
            svgType="error"
            />
      );
    }

    if(success){
      return (
        <Mymodal
        showValue={this.props.contacted}
        title="Thank you!"
        description="We look forward to meeting you. In the meantime, check out
        our store for some cool stuff like customized Beers&Brewskies Coasters!
        We will be in touch with you soon!"
        />
      )
    }

      return (
        <Modal show={true}
          onHide={() => history.push('/shop')}
          aria-labelledby="contained-modal-title-vcenter"
          centered
          >
          <Modal.Header closeButton>
            <Modal.Title>
              Contact Us
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <ContactForm onSubmit={this.onSubmit} loading={loading}/>
          </Modal.Body>
        </Modal>
    )
  }
}

// const mapStateToProps = (state) => {
//   return {
//     contacted: state.contact.contacted,
//     contactInfo: state.contact.contactInfo
//   }
// };


export default connect(null,{ contact })(Contact);
